mapper.update:
UPDATE CHATMEMBER
SET
MSG_COUNT = MSG_COUNT + 1
WHERE USERID != #{userid}
AND CHAT_NO = #{chat_no}

mapper.select:
	SELECT A.*, B.MSG_COUNT FROM CHATGROUP A, CHATMEMBER B
	WHERE A.CHAT_NO = B.CHAT_NO AND
    B.USERID = #{userid}

mapper.update:
UPDATE CHATMEMBER
SET
MSG_COUNT = 0
WHERE USERID = #{userid}
AND CHAT_NO = #{chat_no}

mapper.select:
select count(*) as cnt
from chatmember
where chat_no = #{chat_no}

mapper.update:
UPDATE CHATMESSAGE
SET UNREAD_COUNT = UNREAD_COUNT - 1
WHERE CHAT_NO = #{chat_no}
AND MESSAGE_ID = #{message_id}

mapper.delete:
DELETE FROM UNREADMEMBER
WHERE CHAT_NO = #{chat_no}
AND USERID = #{userid}

mapper.select:
SELECT MESSAGE_ID FROM UNREADMEMBER
WHERE CHAT_NO = #{chat_no}
AND USERID = #{userid}
